package Operators;

public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int result = 1 + 2;
		System.out.println(result);
		int previousResult = result;
		System.out.println(previousResult);
		result = result +1;
		System.out.println(result);
		
		result = 4 % 3;
		System.out.println(result);
		
		result++;
		System.out.println(result);
		
		result--;
		System.out.println(result);
		
		result += 2;
		System.out.println(result);
		
		result *= 10;
		System.out.println(result);
		
		result /= 3;
		System.out.println(result);
		
		result -= 2;
		System.out.println(result);

	}

}
